(()=>{"use strict";const e=()=>{document.getElementById("main").innerHTML="      \n    <img src='img/home.jpg' alt='Picture of Brock' class='home-img'>\n    <div id=\"home-text\">\n      <h1>Brock's Bakery</h1>\n      <p>\n        The capacity of Brock's bag is endless. It's capable of containing\n        full tea-sets, tables, and cooking utensils. Anything you could\n        need!\n      </p>\n    </div>"};document.querySelector(".content").innerHTML="      \n  <header>\n    <nav>\n      <ul>\n        <li><a href=\"#\" id='home'>Home</a></li>\n        <li><a href=\"#\" id='menu'>Menu</a></li>\n        <li><a href=\"#\" id='contact'>Contact Us</a></li>\n      </ul>\n    </nav>\n  </header>\n  <main id='main'>\n  </main>",e();const n=document.getElementById("home"),i=document.getElementById("menu"),t=document.getElementById("contact");n.addEventListener("click",e),i.addEventListener("click",(()=>{document.getElementById("main").innerHTML="\n  <img src='img/menu.jpg' alt='Picture of Brock' class='home-img'>\n  <div id='menu-items'>\n  <h1>Menu</h1>\n    <p>Each item comes with your choice of tea, coffee, or water.</p>\n      <ul>\n        <li>\n          <div class='menu-item'>\n            <h3>Cream Stew</h3>\n            <div class='description'>\n              <img src='img/cream-stew.png' alt='Cream Stew'>\n              <p>Brock's Cream Stew includes carrots, broccoli, potato chunks, some chicken, and a little bit of yellow onion. Made fresh outdoors on a wood fire.</p>\n            </div>\n          </div>\n        </li>\n\n        <li>\n          <div class='menu-item'>\n            <h3>Onigiri/Rice Balls</h3>\n              <div class='description'>\n                <img src='img/rice_balls.png' alt='Onigiri/Rice Balls'>\n                <p>Brock's Onigiri is made using rice rolled into a triangle shape. Filled with your choice of salmon, chicken, tuna shrimp, or pickled plums.</p>\n            </div>\n          </div>\n        </li>\n\n        <li>\n          <div class='menu-item'>\n            <h3>Jelly Donuts</h3>\n            <div class='description'>\n              <img src='img/jelly-donut.jpg' alt='Jelly Donuts'>\n              <p>Brock's Jelly Donuts may look identical to the Onigiri, but they are nothing a like. These are real donuts filled with jelly then topped with powdered sugar and coconut.</p>\n            </div>\n          </div>\n        </li>\n      </ul>\n    </div>"})),t.addEventListener("click",(()=>{document.getElementById("main").innerHTML="\n  <img src='img/contact.jpg' alt='Picture of Brock' class='home-img'>\n  <div id='contact'>\n  <h1>Contact</h1>\n    <p>Each message can be replied by email or hand delivered by our Dragonite.</p>\n    <form>\n      <label for='name'>Name:</label>\n      <input type='text' id='name' name='name'>\n\n      <label for='reply'>Email OR Address:</label>\n      <input type='text' id='reply' name='reply'>\n\n      <label for='message'>Message:</label>\n      <textarea id='message' name='message' rows='4' cols='25'></textarea>\n\n      <input type='submit' value='Submit'>\n    </form>\n  </div>\n  "}))})();